mapper.update:
UPDATE article
updateDate = NOW(), title = #{title}, body = #{body},
WHERE id = #{id}

UPDATE article
SET delDate = NOW(),
delStatus = 1
WHERE id = #{id}
AND memberId = #{memberId}
AND boardId = #{boardId}

UPDATE article
SET hit = hit + 1
WHERE id = #{id}

mapper.select:
SELECT
IFNULL(SUM(AL.point), 0) AS `point`
FROM articleLike AS AL
WHERE
AL.articleId = #{id}
AND AL.memberId = #{memberId}
