mapper.select:
SELECT id
FROM board
WHERE name = #{name}

mapper.update:
UPDATE boardApplyDoc
SET
updateDate = NOW(),
applyStatus = 1,
boardId = #{boardId}
WHERE memberId = #{memberId}
AND name = #{name}

UPDATE boardApplyDoc
SET
updateDate = NOW(),
delStatus = 1
WHERE memberId = #{memberId}
AND name = #{name}
AND boardId = #{boardId}
