mapper.select:
SELECT loginId
FROM member
WHERE name = #{name}
AND email = #{email}

SELECT id
FROM member
WHERE loginId = #{loginId}
AND name = #{name}
AND email = #{email}

mapper.update:
UPDATE member
SET updateDate = NOW(),
loginPw = #{loginPwReal}
WHERE id = #{id}

UPDATE member
SET updateDate = NOW(),
nickname = #{nickname},
email = #{email}
WHERE id = #{id}
